import React from `react`;

class App extends React.component {
    constructor(props) {
        super.this()
            this.handleClick = this.handleClick.bind(this)
    }

    handleClick() {
        console.log('clicou aqui!')
    }

    render() {
        return <button onClick={this.handleClick}>Clique aqui!</button>
    }
}

export default App;
